mapper.insert:
INSERT INTO board (board_Id, board_title, board_contents, board_thumbnail, board_price, user_Id
) values ( null, #{board_title}, #{board_contents}, #{board_thumbnail}, #{board_price}, #{user_Id} )
SELECT LAST_INSERT_ID()

mapper.select:
SELECT board_id,board_title,board_price,board_thumbnail FROM board
where ( board_title LIKE CONCAT('%', #{keyword}, '%') OR board_contents LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY board_id DESC LIMIT #{pagination.limitStart}, #{recordSize}

mapper.update:
update board set
board_title = #{board_title},
board_thumbnail = #{board_thumbnail},
board_contents = #{board_contents},
board_price = #{board_price}
where board_id = #{board_Id}
