mapper.insert:
INSERT INTO comment (comment_Id, board_Id, user_Id, comment_contents
) values ( null, #{boardId}, #{userId}, #{contents}
)

mapper.update:
update comment set
comment_status = 1
where board_Id = #{boardId} and comment_Id = #{commentId}

update comment set
comment_contents = #{content}
where board_Id = #{boardId} and comment_Id = #{commentId}
